mapper.select:
select A.id,A.name,A.cbrands,B.name brand_name,A.maxweight,A.introduce,A.images,A.expdbcodes,A.visits,A.remarks
from `nsl_crane` A
left join `nsl_cbrands` B on A.cbrands = B.id
where A.kbn != '9'

    and A.cbrands = #{cbrandid}


    and A.id = #{craneid}


    and name like "%"#{craneName,jdbcType=VARCHAR}"%"

limit #{pageindex},#{pagesize};

select count(1)
from `nsl_crane`
where kbn != '9'

    and cbrands = #{cbrandid}


    and id = #{craneid}


    and name like "%"#{craneName,jdbcType=VARCHAR}"%"

;
