mapper.select:
select id,name,cbrands,maxweight,introduce,images,expdbcodes,visits,remarks
from `nsl_crane`
where kbn != '9'

    and cbrands = #{cbrandid}


    and id = #{craneid}


    and name like "%"#{craneName,jdbcType=VARCHAR}"%"

limit #{pageindex},#{pagesize};

select count(1)
from `nsl_crane`
where kbn != '9'

    and cbrands = #{cbrandid}


    and id = #{craneid}


    and name like "%"#{craneName,jdbcType=VARCHAR}"%"

;
